using Microsoft.AspNetCore.Authorization;
using Microsoft.AspNetCore.Http;
using Microsoft.AspNetCore.Mvc;
using Microsoft.Extensions.Logging;
using Qhatu.Domain.Models;
using Qhatu.Infrastructure.UnitOfWork;
using System;
using System.Collections.Generic;
using System.Linq;
using System.Threading.Tasks;
using static System.Net.Mime.MediaTypeNames;

namespace QhatuRestService.Controllers
{
    [Route("api/[controller]")]
    [ApiController]
    [Authorize]
    public class UsuarioController : ControllerBase
    {
        private readonly IUnitOfWork unitOfWork;
        private readonly ILogger<ProductoController> logger;
        public UsuarioController(IUnitOfWork unitOfWork, ILogger<ProductoController> logger)
        {
            this.unitOfWork = unitOfWork;
            this.logger = logger;
        }
        [HttpGet]
        public IActionResult AllUsuarios()
        {
            var usuarios = unitOfWork.UsuarioRepository.GetAll();
            return Ok(usuarios);
        }
        [HttpGet("{id}")]
        public IActionResult GetUsuario(int id)
        {
            var usuario = unitOfWork.UsuarioRepository.GetById(id);
            return Ok(usuario);
        }
        [HttpPost("/login")]
        public IActionResult InicioSesion(Usuario usuario)
        {
            var result = unitOfWork.UsuarioRepository.InicioSesion(usuario.Username,usuario.Password);
            return Ok(result);
        }
        [HttpPost]
        public IActionResult AddUsuario(Usuario usuario)
        {
            if (usuario.Foto==null)
            {
                usuario.Foto = "https://nearlab-webclient.azurewebsites.net\\images\\FotoDefecto.jpg";
                //usuario.Foto = Convert.FromBase64String("");
            }
            int result = unitOfWork.UsuarioRepository.Add(usuario);
            var cliente = new Cliente { UsuarioId=result};
            int result2 = unitOfWork.ClienteRepository.Add(cliente);
            unitOfWork.Commit();
            return CreatedAtAction(nameof(AddUsuario), result);
        }
        [HttpPut]
        public IActionResult UpdateUsuario(Usuario usuario)
        {
            int result = unitOfWork.UsuarioRepository.Update(usuario);
            unitOfWork.Commit();
            return CreatedAtAction(nameof(UpdateUsuario), result);
        }
        [HttpDelete("{id}")]
        public IActionResult DeleteUsuarioById(int id)
        {
            int result = unitOfWork.UsuarioRepository.DeleteById(id);
            unitOfWork.Commit();
            return CreatedAtAction(nameof(DeleteUsuarioById), result);
        }
    }
}
